import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { DataService } from '../../../core/data.service';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css']
})
export class ProductDetailComponent implements OnInit {
  product: any;

  constructor(private route: ActivatedRoute, private dataService: DataService) { }

  ngOnInit(): void {
    const productId = Number(this.route.snapshot.paramMap.get('id'));
    this.dataService.getProducts().subscribe((products) => {
      this.product = products.find((product: { id: number; }) => product.id === productId);
    });
  }
}
<div class="container d-flex justify-content-center align-items-center vh-100">
    <div class="card shadow p-4 w-50">
        <h2 class="text-center text-primary mb-4">Product Details</h2>

        <ul class="list-group">
            <li class="list-group-item"><strong>Name:</strong> {{ product?.name }}</li>
            <li class="list-group-item"><strong>Description:</strong> {{ product?.description }}</li>
            <li class="list-group-item"><strong>Manufacturer:</strong> {{ product?.manufacturer }}</li>
            <li class="list-group-item"><strong>Price:</strong> ${{ product?.price }}</li>
            <li class="list-group-item"><strong>Quantity:</strong> {{ product?.quantity }}</li>
        </ul>

        <button class="btn btn-outline-primary mt-3 w-100">Back to Products</button>
    </div>
</div>
