import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, throwError } from 'rxjs';
import { catchError, map } from 'rxjs/operators';
import { switchMap } from 'rxjs/operators';


@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private apiUrl = 'http://localhost:3000';

  constructor(private http: HttpClient) { }

  login(email: string, password: string): Observable<boolean> {
    return this.http.get<any[]>(`${this.apiUrl}/users`).pipe(
      map((users) => {
        const user = users.find(
          (u: any) => u.email === email && u.password === password
        );
        if (user) {
          localStorage.setItem('currentUser', JSON.stringify(user));
          return true;
        }
        return false;
      }),
      catchError(this.handleError)
    );
  }

  logout(): void {
    localStorage.removeItem('currentUser');
  }


  isLoggedIn(): boolean {
    return !!localStorage.getItem('currentUser');
  }

  register(user: any): Observable<any> {
    return this.http.get<any[]>(`${this.apiUrl}/users`).pipe(
      map((users) => {
        // Check if email already exists
        const existingUser = users.find((u) => u.email === user.email);
        if (existingUser) {
          throw new Error('Email already exists! Please use a different email.');
        }

        // Assign a unique ID (max ID + 1)
        const newUser = { ...user, id: users.length ? Math.max(...users.map(u => u.id)) + 1 : 1 };

        return newUser;
      }),
      switchMap((newUser) => this.http.post(`${this.apiUrl}/users`, newUser)),
      catchError(this.handleError)
    );
  }


  private handleError(error: HttpErrorResponse) {
    if (error.status === 0) {
      console.error('Network Error:', error.error);
      return throwError(() => new Error('❌ Network error! Please check your connection.'));
    } 

    if (error.error instanceof ErrorEvent) {
      // Handle client-side or network errors
      console.error('Client-side error:', error.error.message);
      return throwError(() => new Error('⚠️ Something went wrong on your side! Please check and try again.'));
    }

    if (error.status >= 400 && error.status < 500) {
      // Check if error message contains "Email already exists"
      if (error.error && error.error.message.includes('Email already exists')) {
        return throwError(() => new Error('⚠️ Email already exists! Try a different email.'));
      }

      return throwError(() => new Error('❌ Client-side error! Check your request.'));
    } 

    return throwError(() => new Error('🔥 Server error! Please try again later.'));
  }

  }
}
