import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, throwError } from 'rxjs';
import { catchError, switchMap, map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private apiUrl = 'http://localhost:3000';

  constructor(private http: HttpClient) {}

  /** ✅ Check if Email Exists Before Registering */
  register(user: any): Observable<any> {
    return this.http.get<any[]>(`${this.apiUrl}/users`).pipe(
      map((users) => {
        const existingUser = users.find((u) => u.email === user.email);
        if (existingUser) {
          throw new Error('User with this email already exists!'); // Prevent duplicate users
        }
        return user;
      }),
      switchMap((newUser) => this.http.post(`${this.apiUrl}/users`, newUser)), // Register user if email is unique
      catchError(this.handleError) // Handle API errors
    );
  }

  /** ✅ Handle API Errors */
  private handleError(error: HttpErrorResponse) {
    if (error.status === 0) {
      return throwError(() => new Error('Network error! Check your internet connection.'));
    } else if (error.status === 400) {
      return throwError(() => new Error('Invalid data! Please check your input.'));
    } else {
      return throwError(() => new Error('Server error! Please try again later.'));
    }
  }
}
