import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService } from '../../../core/data.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-update-product',
  templateUrl: './update-product.component.html',
  styleUrls: ['./update-product.component.css']
})
export class UpdateProductComponent implements OnInit {
  updateProductForm: FormGroup;
  productId: number | null = null;

  constructor(
    private route: ActivatedRoute,
    private dataService: DataService,
    private fb: FormBuilder,
    private router: Router
  ) {
    this.updateProductForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      description: ['', Validators.required],
      manufacturer: ['', Validators.required],
      price: [0, [Validators.required, Validators.min(0)]],
      quantity: [1, [Validators.required, Validators.min(1)]]
    });
  }

  ngOnInit(): void {
    // Get product ID from route params
    this.productId = Number(this.route.snapshot.paramMap.get('id'));

    if (this.productId) {
      // Load product data from service
      console.log(this.productId, typeof this.productId);

      this.dataService.getProductById(this.productId).subscribe(
        (product) => {
          if (product) {
            // Populate the form with product data
            this.updateProductForm.patchValue(product);
          } else {
            alert('Product not found.');
            this.router.navigate(['/inventory']);
          }
        },
        (error) => {
          console.error('Error loading product:', error);
          alert('An error occurred while loading the product.');
          this.router.navigate(['/inventory']);
        }
      );
    }
  }

  onUpdate(): void {
    if (this.updateProductForm.valid && this.productId !== null) {
      // Update product data
      const updatedProduct = { id: this.productId, ...this.updateProductForm.value };
      this.dataService.updateProduct(updatedProduct).subscribe(
        () => {
          alert('Product updated successfully!');
          this.router.navigate(['/inventory']);
        },
        (error) => {
          console.error('Error updating product:', error);
          alert('An error occurred while updating the product.');
        }
      );
    } else {
      alert('Please correct the errors in the form.');
    }
  }
}
<div class="container mt-4">
    <h2 class="text-center text-black mb-4">Update Product</h2>

    <form [formGroup]="updateProductForm" (ngSubmit)="onUpdate()" class="card p-4 shadow">
        <div class="mb-3">
            <label class="form-label">Name:</label>
            <input type="text" class="form-control" formControlName="name" placeholder="Enter product name" />
            <div *ngIf="updateProductForm.get('name')?.invalid && updateProductForm.get('name')?.touched" class="text-danger">
                <small *ngIf="updateProductForm.get('name')?.errors?.['required']">Product name is required.</small>
                <small *ngIf="updateProductForm.get('name')?.errors?.['minlength']">Name must be at least 3 characters long.</small>
            </div>
        </div>

        <div class="mb-3">
            <label class="form-label">Description:</label>
            <input type="text" class="form-control" formControlName="description" placeholder="Enter product description" />
            <div *ngIf="updateProductForm.get('description')?.invalid && updateProductForm.get('description')?.touched" class="text-danger">
                <small *ngIf="updateProductForm.get('description')?.errors?.['required']">Description is required.</small>
            </div>
        </div>

        <div class="mb-3">
            <label class="form-label">Manufacturer:</label>
            <input type="text" class="form-control" formControlName="manufacturer" placeholder="Enter manufacturer name" />
            <div *ngIf="updateProductForm.get('manufacturer')?.invalid && updateProductForm.get('manufacturer')?.touched" class="text-danger">
                <small *ngIf="updateProductForm.get('manufacturer')?.errors?.['required']">Manufacturer is required.</small>
            </div>
        </div>

        <div class="mb-3">
            <label class="form-label">Price:</label>
            <input type="number" class="form-control" formControlName="price" placeholder="Enter price" />
            <div *ngIf="updateProductForm.get('price')?.invalid && updateProductForm.get('price')?.touched" class="text-danger">
                <small *ngIf="updateProductForm.get('price')?.errors?.['required']">Price is required.</small>
                <small *ngIf="updateProductForm.get('price')?.errors?.['min']">Price cannot be negative.</small>
            </div>
        </div>

        <div class="mb-3">
            <label class="form-label">Quantity:</label>
            <input type="number" class="form-control" formControlName="quantity" placeholder="Enter quantity" />
            <div *ngIf="updateProductForm.get('quantity')?.invalid && updateProductForm.get('quantity')?.touched" class="text-danger">
                <small *ngIf="updateProductForm.get('quantity')?.errors?.['required']">Quantity is required.</small>
                <small *ngIf="updateProductForm.get('quantity')?.errors?.['min']">Quantity must be at least 1.</small>
            </div>
        </div>

        <div class="text-center">
            <button type="submit" class="btn btn-success" [disabled]="updateProductForm.invalid">Update Product</button>
        </div>
    </form>
</div>
